var cameraFacing=!1;window.addEventListener('DOMContentLoaded',function(){var change_btn=document.getElementById('swCamera');change_btn.addEventListener("click",function(e){e.preventDefault();var vi=document.querySelector('video');const mode=cameraFacing?"environment":"user";cameraFacing?document.querySelector('video').classList.remove("active"):document.querySelector('video').classList.add("active");cameraFacing?document.querySelector('canvas').classList.remove("active"):document.querySelector('canvas').classList.add("active");stopStreamedVideo(vi);navigator.mediaDevices.getUserMedia({video:{facingMode:mode}}).then(stream=>vi.srcObject=stream).catch(err=>alert(`${err.name} ${err.message}`));cameraFacing=!cameraFacing})});function stopStreamedVideo(videoElem){let stream=videoElem.srcObject;let tracks=stream.getTracks();tracks.forEach(function(track){track.stop()});videoElem.srcObject=null} window.addEventListener('DOMContentLoaded',function(){var image=document.getElementById('swScrshot');image.addEventListener("click",function(e){var res=window.confirm("スナップショットを行います。");if(res==!0){e.preventDefault();var video=document.querySelector('video');var snap=takeSnapshot(video);location.reload();var win=window.open();win.document.write("<img src='"+snap+"'/>")}})});function takeSnapshot(video){var resizedCanvas=document.createElement("canvas");var resizedContext=resizedCanvas.getContext("2d");var width=video.videoWidth;var height=video.videoHeight;var aScene=document.querySelector("a-scene").components.screenshot.getCanvas("perspective");if(width&&height){resizedCanvas.width=width;resizedCanvas.height=height;resizedContext.drawImage(video,0,0,width,height);if(width>height){resizedContext.drawImage(aScene,0,0,width,height)}else{var scale=height/width;var scaledWidth=height*scale;var marginLeft=(width-scaledWidth)/2;resizedContext.drawImage(aScene,marginLeft,0,scaledWidth,height)} return resizedCanvas.toDataURL('image/png')}}